package com.sypark.problem;

import java.net.URI;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.time.Duration;

import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.context.annotation.Bean;
import org.springframework.http.MediaType;
import org.springframework.web.reactive.function.client.WebClient;
import org.springframework.web.reactive.function.server.*;

import lombok.extern.slf4j.Slf4j;
import reactor.core.publisher.Mono;

@Slf4j
@SpringBootApplication

public class ProblemApplication {
	// 15k dummy payload
	String bigpayload = "";

	public static void main(String[] args) {
		SpringApplication.run(ProblemApplication.class, args);
	}

	@Bean
	public RouterFunction<ServerResponse> routes() {
		return RouterFunctions.route()
				.GET("/workloadwexcall", this::handlerWorkloadWithExtCall)
				.GET("/workloadwoexcall", this::handlerWorkloadWithoutExtCall)
				.build();
	}


	private Mono<ServerResponse> handlerWorkloadWithExtCall(ServerRequest request)
	{
		return Mono.just("")
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(this::workloadWithExtCall)
				.flatMap(hashes -> ServerResponse.ok().contentType(MediaType.TEXT_PLAIN).bodyValue(bigpayload + bigpayload +hashes));
	}
	private Mono<ServerResponse> handlerWorkloadWithoutExtCall(ServerRequest request)
	{
		return Mono.just("")
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(this::workloadWithoutExtCall)
				.flatMap(hashes -> ServerResponse.ok().contentType(MediaType.TEXT_PLAIN).bodyValue(bigpayload + bigpayload +hashes));
	}


	WebClient webClient = WebClient.builder().baseUrl("http://127.0.0.1:8088/").build();
	private Mono<String> workloadWithExtCall(String prevHash) {
		return webClient.get().uri(URI.create("http://127.0.0.1:8088/data"))
				.retrieve().bodyToMono(String.class)
				.map(data -> md5(data+prevHash));
	}
	private Mono<String> workloadWithoutExtCall(String prevHash) {
		return Mono.just(bigpayload+Long.toString(System.currentTimeMillis())).delayElement(Duration.ofMillis(1))
				.map(data -> md5(data+prevHash));
	}


	private String md5(String in) {
		StringBuffer hexString = new StringBuffer();
		try {
			// MD5 Hash
			MessageDigest digest = java.security.MessageDigest.getInstance("MD5");
			digest.update(in.getBytes());
			byte messageDigest[] = digest.digest();

			// Hex String
			for (int i = 0; i < messageDigest.length; i++) {
				hexString.append(Integer.toHexString((messageDigest[i] & 0xff)+0x100).substring(1));
			}
		} catch (NoSuchAlgorithmException e) {
			e.printStackTrace();
		}
		return hexString.toString();
	}

}
